import React from "react"

const SearchArea = (props) => {	
	return(
		<div className="search-area">
			<form action="">
				<input onChange={props.searchHandle} type="text" placeholder="Enter a book name"/>
				<button type="submit" onCick={props.bookSearch}>Search</button>
			</form>
		</div>
	)
}

export default SearchArea
