import React from "react"
import SearchArea from "./SearchArea"


class Books extends React.Component{
	constructor(props){
			super(props);
			this.state = {
				books: [],
				searchField: ''
			}
	};

	searchHandle = (e) => {
		this.setState({searchField: e.target.value})
	}

	render(){
		return(
			<div>
				<SearchArea searchHandle={this.searchHandle} />
			</div>
		)
	}
}

export default Books
