import React, { Component } from "react";
import ReactDOM from 'react-dom';

class rss extends Component {
    constructor() {
      super();
      this.myref = React.createRef();
      this.xhr = {}
      var i = 0
      this.obj = {
        divinner: {},
        getNews : () =>
        {
            this.xhr = new XMLHttpRequest();
            this.xhr.onreadystatechange =  this.obj.processNews;
            // Using a cors proxy to access the rss feed
            this.xhr.open("GET","https://cors-anywhere.herokuapp.com/https://blog.bookstellyouwhy.com/rss.xml",true);
            this.xhr.send();
        },
        processNews : () =>
        {
            if (this.xhr.readyState == 4 && this.xhr.status == 200)
            {
                let root = this.xhr.responseXML.documentElement;
                let items = root.getElementsByTagName("item")[i];
                let title = root.getElementsByTagName("title")[i];
                let link = root.getElementsByTagName("link")[i];
                let ele = document.createElement("a");
                ele.innerHTML = title.firstChild.nodeValue;
                ele.href = link.firstChild.nodeValue;
               
                let eled = document.createElement("div");
                eled.appendChild(ele);
                eled.className = "marquee";
                console.log(eled);

                this.obj.divinner.innerHTML = "";
                this.obj.divinner.appendChild(eled);
            }
           i = i+1
        }
     };
    }
 

    componentDidMount() {
        this.obj.divinner = this.myref.current;
        this.obj.divinner.style.left = window.innerWidth - 5 + "px";
        this.obj.getNews();
        setInterval(this.obj.getNews,1000);
    }
 
    render() {
      return (
            <div ref = {this.myref}>
            </div>
      );
    }
  }

export default rss;
