import React, { useState } from 'react';
import { Form, Input, Button, Image } from 'semantic-ui-react';
import Gallery from 'react-grid-gallery';

export const BookEntry = ()  => {
  const [title, setTitle] = useState(''); //  Empty String
  const [outputURL1, setOutputURL1] = useState('');
  const [outputURL2, setOutputURL2] = useState('');
  const [outputURL3, setOutputURL3] = useState('');
  const [outputURL4, setOutputURL4] = useState('');
  const [outputURL5, setOutputURL5] = useState('');
  var nameArr = [];

  return (
  <div id="form">
    <Form>
      <Form.Field>
        <Input
        placeholder="Enter one of your favorite book titles "
        value={title}
        onChange={event => setTitle(event.target.value)}
        />
      </Form.Field>

      <Form.Field>
        <Button onClick= {async () => {
          const book = {title};
          const response = await fetch("/input_book", {
            method: "POST",
            headers: {
              "Content_Type": "application/json"
            },
            body:
              JSON.stringify(book)
            })

          if (response.ok) {
            var form = document.getElementById("form") 
            // console.log("Response Worked! ");
            // console.log(JSON.stringify(response.url));
            // console.log(response);
            setTitle("We found your favorite book!")
            fetch("/novel_novel").then(response =>
              response.json().then(data => {
              nameArr = data.image_url.split(";")

              setOutputURL1(nameArr[0]);
              setOutputURL2(nameArr[1]);
              setOutputURL3(nameArr[2]);
              setOutputURL4(nameArr[3]);
              setOutputURL5(nameArr[4]);
                  // console.log(outputURL)
              })
            ); 
          }
          else {
            console.log("Title not found")
            setTitle("We did not find this title. Please try again!")
          }
        const IMAGES = [{
          src: {outputURL1},
          thumbnail: {outputURL1},
          thumbnailWidth: 400,
          thumbnailHeight: 320,
          isSelected: true,
        },
        {
          src: {outputURL2},
          thumbnail: {outputURL2},
          thumbnailWidth: 150,
          thumbnailHeight: 320,
          isSelected: true,
        },
        {
          src: {outputURL3},
          thumbnail: {outputURL3},
          thumbnailWidth: 150,
          thumbnailHeight: 320,
          isSelected: true,
        },
        {
          src: {outputURL4},
          thumbnail: {outputURL4},
          thumbnailWidth: 150,
          thumbnailHeight: 320,
          isSelected: true,
        },
        {
          src: {outputURL5},
          thumbnail: {outputURL5},
          thumbnailWidth: 150,
          thumbnailHeight: 320,
          isSelected: true,
        }
      ] 
        }
      }>
        Add</Button>
      </Form.Field>
    </Form>
    <Gallery images={IMAGES}/>
  </div>
  );
};
