import React from "react"
import fire from "../Fire"
import { BrowserRouter as Router, Link, Route } from "react-router-dom"
import Recommendation from "./Recommendation"
import Books from "./Books"

class Header extends React.Component{

	constructor(props) {
        super(props);
        this.logout = this.logout.bind(this);
    }

    logout() {
        fire.auth().signOut();
    }

    render(){
		return(
			<Router>
				<header>
					<i style={{float: 'left'}} className="fa fa-book fa-2x" aria-hidden="true"></i>
					<h1 style={{float: 'left'}}>Boogle</h1>
					<ul>
						<li><button style={{float: 'right'}}><Link to="/books">Books</Link></button></li>
						<li><button style={{float: 'right'}} onClick={this.logout}>Logout</button></li>
						<li><button style={{float: 'right'}}><Link to="/recommendation">Recommendation</Link></button></li>
					</ul>
				</header>
				<Route exact path="/recommendation" component={ Recommendation }/>
				<Route exact path="/books" component={ Books }/>
			</Router>
		)
	}
}

export default Header
