import React from "react"
import Login from "./Login"
import { BrowserRouter as Router, Link, Route } from "react-router-dom"
import Popup from "reactjs-popup"
import fire from '../Fire';

var sectionStyle = {
	fontFamily: "Handlee",
    color: "#4A1137",
    fontSize: "25px",
}

var button1 = {
  color: "white",
  textTransform: 'uppercase',
  textDecoration: 'none',
  background: '#581845',
  padding: '10px',
  borderRadius: '5px',
  display: 'inline-block',
  border: 'none',
  transition: "all 0.4s ease 0s",
  fontFamily: "Acme",
  paddingRight: "35px", 
  paddingLeft: "35px",
  fontSize: "30px",
  marginLeft: "600px"
}

var button2 = {
  color: "white",
  textTransform: 'uppercase',
  textDecoration: 'none',
  background: '#581845',
  padding: '10px',
  borderRadius: '5px',
  display: 'inline-block',
  border: 'none',
  transition: "all 0.4s ease 0s",
  fontFamily: "Acme",
  paddingRight: "35px", 
  paddingLeft: "35px",
  fontSize: "30px",
}

var input1 = {
  marginTop: "10px",
  width: "700px",
  border: "none",
  borderBottom: "4px solid #581845",
  fontFamily: "Acme",
  fontSize: "30px"
}

var input2 = {
  marginTop: "5px",
  width: "700px",
  border: "none",
  borderBottom: "4px solid #581845",
  fontFamily: "Acme",
  fontSize: "30px"
}


class Rss extends React.Component{
	constructor(){
		super();
		this.myref = React.createRef();
		this.request = {};
		this.obj = {
			state: {
				name: '',
				url: '',
				description: '',
				email:'',
				password:'',
				i:0
		},
		getNews: () => {
			this.request = new XMLHttpRequest()
			this.request.onreadystatechange = this.obj.processNews
			this.request.open("GET", "https://api.rss2json.com/v1/api.json?rss_url=https%3A%2F%2Fblog.bookstellyouwhy.com%2Frss.xml", true);
    		this.request.send();
		},
		processNews: () => {
			if(this.request.readyState == 4 && this.request.status == 200 && this.obj.state.i < 10){
				var myObj = JSON.parse(this.request.responseText)
				var feed = document.getElementById("feed")
				// console.log(myObj.items[this.obj.state.i].title)
				// console.log(myObj.items[this.obj.state.i].link)

        		this.obj.state.name = myObj.items[this.obj.state.i].title
          		this.obj.state.url = myObj.items[this.obj.state.i].link
          		this.obj.state.description = myObj.items[this.obj.state.i].description
        			 
      			var anchor = document.createElement("a")
      			anchor.align = "center"
      			anchor.innerHTML = this.obj.state.name
      			anchor.class = "a"
      			anchor.href = this.obj.state.url
      			var br = document.createElement("br")

      			var desc = document.createElement("p")
      			var desc1 = this.obj.state.description
      			var desc2 = desc1.substring(desc1.indexOf("<p>"), desc1.length)
                // console.log(desc2)
                desc.innerHTML = desc2;
      			feed.appendChild(br)
      			feed.appendChild(anchor)
      			feed.appendChild(desc)
      			this.obj.state.i += 1
				}
			}
		}
	}	

	handleChange1(e) {
     this.obj.state.email = e.target.value;
   	}

   	handleChange2(e) {
     this.obj.state.password = e.target.value;
   	}

  	login(e) {
    	e.preventDefault();
    	fire.auth().signInWithEmailAndPassword(this.obj.state.email, this.obj.state.password).then((u)=>{
    	}).catch((error) => {
        	console.log(error);
      	});
  	}

  	signup(e) {
    	e.preventDefault();
    	fire.auth().createUserWithEmailAndPassword(this.obj.state.email, this.obj.state.password).then((u)=>{
    	}).then((u)=>{console.log(u)})
    	.catch((error) => {
        	console.log(error);
      	})
  	}

	componentDidMount() {
		this.obj.getNews();
        setInterval(this.obj.getNews,1500);
  	}

	render(){
		return (
				<div id="feed" style = { sectionStyle } ref = {this.myref}>
					<br/>
					<h1 style={{marginLeft:"630px"}}> NEWS 
						<Popup trigger = 
						{
							<button style = {button1} position="center">Login</button>
						}
						modal
						closeOnDocumentClick>
						{close => (
							<div>
						        <form>
						          <div>
						            <input  style={input1} onChange={this.handleChange1} value={this.obj.state.email} type="email" name="email" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" />
						          </div>
						          <div className="form-group">
						            <input style={input2} onChange={this.handleChange2} value={this.obj.state.password} type="password" name="password" id="exampleInputPassword1" placeholder="Password" />
						          </div>
						          <div style={{  marginLeft: "175px"}}>
						            <button style={button2} type="submit" onClick={this.login}>Login</button>
						            <button style={button2} onClick={this.signup}>Signup</button>
						          </div>
						        </form>
						    </div>
							)}
						</Popup>
					</h1>
				</div>
		)
	}
}

export default Rss
