import React from "react"
import SearchArea from "./SearchArea"
import request from "superagent"
import ListBooks from "./ListBooks"

class Books extends React.Component{
	constructor(props){
			super(props);
			this.state = {
				books: [],
				searchField: '',
				sort: ''
			}
	};

	searchHandle = (e) => {
		// console.log(e.target.value)
		this.setState({searchField: e.target.value})
	}

	bookSearch = (e) => {
		e.preventDefault()

		request.get("https://www.googleapis.com/books/v1/volumes").query({ q:this.state.searchField })
		.then((data) => {
			console.log(data)
			this.setState({ books: [...data.body.items]}) //'...' -> spread operator(spreading contents of items into the array) 
		})
	}

	render(){
		return(
			<div>
				<SearchArea bookSearch={this.bookSearch} searchHandle={this.searchHandle} />
				<ListBooks books={this.state.books} />
			</div>
		)
	}
}

export default Books
